mapper.select:
select m.model_id, m.mname, m.describtion, m.pcurl, m.pcpath, m.seoword, m.status, m.author, m.phoneurl, m.phpath, m.keyword, date_format(m.addtime,'%Y-%m-%d') as addtime, m.mprice , co.cid, co.cname, co.cssdata , h.hid, h.hname , a.aid, a.aname , iu.counts from model m left join hangye h on m.hid = h.hid left join application a on m.aid = a.aid left join color co on m.cid = co.cid left join view_hotf iu on m.model_id = iu.model_id
m.bid = 2 and m.hid = #{hid} and m.aid = #{aid} and m.cid = #{cid} and m.status = #{status} and m.keyword like concat('%',#{keyword},'%') and m.model_id = iu.model_id order by m.addtime desc
limit #{currentPage} , #{pageSize}
